mapper.select:
select `screening_id`,age,check_date,child_idcard,`history_id`, `righteye_diopter_s`,`righteye_diopter_c`,`righteye_diopter_a`, `lefteye_diopter_s`,`lefteye_diopter_c`,`lefteye_diopter_a`,`righteye_dengxiaoqiujing`, `lefteye_dengxiaoqiujing`,`ou_pd`,`os_pd`,`od_pd`,`righteye_result_type`,`righteye_result_ins`, `lefteye_result_type`,`lefteye_result_ins` from t_child_refraction_screening
and child_idcard = #{childIdcard} and age = #{age}
order by check_date desc limit 1
